let config={iconSize:16,iconColor:"#2067BC",txtLabel:"",txtMessage:"Copied!",txtTime:1500};document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".copy-text").forEach(element=>{const svgIcon=`<svg xmlns="http://www.w3.org/2000/svg" width="${config.iconSize}" height="${config.iconSize}" viewBox="0 0 24 24" fill="none" stroke="${config.iconColor}" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-copy"><rect width="14" height="14" x="8" y="8" rx="2" ry="2"/><path d="M4 16c-1.1 0-2-.9-2-2V4c0-1.1.9-2 2-2h10c1.1 0 2 .9 2 2"/></svg>`,icon=document.createElement("span");icon.innerHTML=svgIcon,icon.classList.add("copy-icon");const label=config.txtLabel;if(""!==label){const textSpan=document.createElement("span");textSpan.classList.add("copy-icon-text"),textSpan.innerText=label,icon.appendChild(textSpan)}element.appendChild(icon),icon.addEventListener("click",()=>{let mainText=element.innerText.trim();if(""!==label){const regex=new RegExp("\\n?"+label+"$");mainText=mainText.replace(regex,"").trim()}navigator.clipboard.writeText(mainText).then(()=>{const popup=document.createElement("span");popup.innerText=config.txtMessage,popup.classList.add("copy-popup"),icon.appendChild(popup),setTimeout(()=>popup.remove(),config.txtTime)})})})});